	/** {@inheritDoc} */
	@Override
	public org.taktik.connector.business.recipe.executor.domain.GetPrescriptionForExecutorResult getPrescription(String rid)
			throws IntegrationModuleException {
		LOG.info("GetPrescriptionForExecutorResult : " + rid);
		org.taktik.connector.business.recipe.executor.domain.GetPrescriptionForExecutorResult result =
			new org.taktik.connector.business.recipe.executor.domain.GetPrescriptionForExecutorResult();
		result.setCreationDate(GregorianCalendar.getInstance());
		result.setEncryptionKeyId(UUID.randomUUID().toString().replaceAll("-", ""));
		result.setPatientId("84071230581l");
		result.setPrescriberId("12659389004l");
		result.setPrescriptionType("P0");
		result.setRid("BEP0" + 
				UUID.randomUUID().toString().replaceAll("-", "").substring(0, 8));
		result.setTimestampingId(UUID.randomUUID().toString().replaceAll("-", ""));
		result.setEncryptionKey("fake key".getBytes());
		result.setSealedContent(MOCK_PRESC.getBytes());
		
		if( getPropertyHandler().hasProperty("MOCK_PRESC") ){
			try {
				LOG.info("Loading prescription from " + getPropertyHandler().getProperty("MOCK_PRESC"));
				result.setPrescription(
						IOUtils.getBytes(
						IOUtils.getResourceAsStream(getPropertyHandler().getProperty("MOCK_PRESC"))));
			} catch (IOException e) {
				e.printStackTrace();
				byte [] b = compressedB64toString(MOCK_PRESC).getBytes();
				result.setSealedContent(b);
				result.setPrescription(b);
			}
		}else{
			byte [] b = compressedB64toString(MOCK_PRESC).getBytes();
			result.setSealedContent(b);
			result.setPrescription(b);
		}
		return result;
	}

	/** {@inheritDoc} */
	@Override
	public List<ListNotificationsItem> listNotifications(boolean readFlag)
			throws IntegrationModuleException {
		LOG.info("listNotifications : " + readFlag);
		
		byte[] bytes = null;
		if( getPropertyHandler().hasProperty("MOCK_NOTIF")  ){
			try {
				LOG.info("Loading prescription from " + getPropertyHandler().getProperty("MOCK_NOTIF"));
				bytes = IOUtils.getBytes(
								IOUtils.getResourceAsStream(getPropertyHandler().getProperty("MOCK_NOTIF")));
			} catch (IOException e) {
				e.printStackTrace();
				bytes =	compressedB64toString(MOCK_NOTIF).getBytes();
			}
		}else{
			bytes =	compressedB64toString(MOCK_NOTIF).getBytes();
		}
		
		List<ListNotificationsItem> list = new ArrayList<ListNotificationsItem>();
		for( int i = 0 ; i < 10 ; i++){
			org.taktik.connector.business.recipe.executor.domain.ListNotificationsItem item = new org.taktik.connector.business.recipe.executor.domain.ListNotificationsItem(new ListNotificationsItem());
			item.setContent(bytes);
			item.setSentBy("12659389004l");
			item.setSentDate(new Date());
			list.add(item);
		}
		return list;
	}

	
	/**
	 * Compressed b64to string.
	 *
	 * @param s the s
	 * @return the string
	 */
	private String compressedB64toString(String s){
		byte[] bytes = Base64.decode( 
				s.getBytes());
		try {
			bytes = IOUtils.decompress(bytes);
		} catch (IOException e) {
			e.printStackTrace();
		}
		return new String (bytes);
	}
	
	
