package org.taktik.connector.business.recipe.prescriber.mock;

import java.io.IOException;
import java.util.ArrayList;
import java.util.GregorianCalendar;
import java.util.List;

import org.apache.log4j.Logger;

import org.bouncycastle.util.encoders.Base64;

import org.taktik.connector.business.recipeprojects.core.exceptions.IntegrationModuleException;
import org.taktik.connector.business.recipeprojects.core.utils.IOUtils;
import org.taktik.connector.business.recipeprojects.core.utils.PropertyHandler;
import org.taktik.connector.business.recipe.prescriber.PrescriberIntegrationModuleImpl;
import be.recipe.services.prescriber.GetPrescriptionForPrescriberResult;
import be.recipe.services.prescriber.ListFeedbackItem;


/**
 * The Class SealedPrescriberServicesClient.
 */
public class PrescriberIntegrationModuleMock extends PrescriberIntegrationModuleImpl {

	/** The Constant LOG. */
	private final static Logger LOG = Logger.getLogger(PrescriberIntegrationModuleMock.class);


	/**
	 * Instantiates a new prescriber integration module.
	 * 
	 * @throws IntegrationModuleException the integration module exception
	 */
	public PrescriberIntegrationModuleMock() throws IntegrationModuleException {
		super();
	}

	/**
	 * Compressed b64to string.
	 *
	 * @param s the s
	 * @return the string
	 */
	private String compressedB64toString(String s){
		byte[] bytes = Base64.decode( 
				s.getBytes());
		try {
			bytes = IOUtils.decompress(bytes);
		} catch (IOException e) {
			e.printStackTrace();
		}
		return new String (bytes);
	}

	@Override
	protected void init() throws IntegrationModuleException {
	}

	@Override
	public String createPrescription(boolean feedbackRequested, String patientId,
			byte[] prescription, String prescriptionType)
	throws IntegrationModuleException {
		LOG.info("createPrescription : " + prescriptionType);
		return "BEPP" + 
		java.util.UUID.randomUUID().toString().replaceAll("-","").substring(0,8);
	}

	/** {@inheritDoc} */
	@Override
	public GetPrescriptionForPrescriberResult getPrescription(String rid)
	throws IntegrationModuleException {
		LOG.info("getPrescription : " + rid);
		GetPrescriptionForPrescriberResult p = new GetPrescriptionForPrescriberResult();
		p.setRid(rid);
		p.setPatientId("84071230581");
		p.setCreationDate(GregorianCalendar.getInstance());
		
		if( getPropertyHandler().hasProperty("MOCK_PRESC") ){
			try {
				LOG.info("Loading prescription from " + getPropertyHandler().getProperty("MOCK_PRESC"));
				p.setPrescription(
						IOUtils.getBytes(
								IOUtils.getResourceAsStream(getPropertyHandler().getProperty("MOCK_PRESC"))));
			} catch (IOException e) {
				LOG.error("IOException",e);
				p.setPrescription(
						compressedB64toString("").getBytes());
			}
		}else{
			p.setPrescription(
					compressedB64toString("").getBytes());
		}
		p.setEncryptionKeyId(java.util.UUID.randomUUID().toString().replaceAll("-","").substring(0,8));
		return p;
	}

	/** {@inheritDoc} */
	@Override
	public List<ListFeedbackItem> listFeedback(boolean readFlag)
	throws IntegrationModuleException {
		List<ListFeedbackItem> list = new ArrayList<ListFeedbackItem>();
		for( int i = 0 ; i < 10 ; i ++ ){
			ListFeedbackItem item = new ListFeedbackItem();
			if( getPropertyHandler().getProperty("MOCK_FEEDB") != null ){
				try {
					LOG.info("Loading prescription from " + getPropertyHandler().getProperty("MOCK_FEEDB"));
					item.setContent(
							IOUtils.getBytes(
									IOUtils.getResourceAsStream(getPropertyHandler().getProperty("MOCK_FEEDB"))));
				} catch (IOException e) {
					e.printStackTrace();
					item.setContent(compressedB64toString("").getBytes());
				}
			}else{
				item.setContent(compressedB64toString("").getBytes());
			}
			item.setRid("BEPPaabbccd" + i);
			item.setSentBy("20795810001l");
			item.setSentDate(GregorianCalendar.getInstance());
			list.add(item);
		}
		return list;
	}

	/** {@inheritDoc} */
	@Override
	public List<String> listOpenPrescription(String patientId)
	throws IntegrationModuleException {
		List<String> list = new ArrayList<String>();
		for( int i = 0; i <10; i++){
			list.add( "BEPP" + 
					java.util.UUID.randomUUID().toString().replaceAll("-","").substring(0,8));
		}
		return list;
	}

	/** {@inheritDoc} */
	@Override
	public void prepareCreatePrescription(String patientId,String pp) {
		LOG.info("prepareCreatePrescription : " + patientId);
	}

	/** {@inheritDoc} */
	@Override
	public void revokePrescription(String rid, String reason)
	throws IntegrationModuleException {
		LOG.info("revokePrescription : " + rid);
	}

	/** {@inheritDoc} */
	@Override
	public void sendNotification(byte[] notificationText, String patientId,
			String executorId) throws IntegrationModuleException {
		LOG.info("sendNotification");
	}

	/** {@inheritDoc} */
	@Override
	public void updateFeedbackFlag(String rid, boolean feedbackAllowed)
	throws IntegrationModuleException {
		LOG.info("updateFeedbackFlag");
	}
	
	/** {@inheritDoc} */
	@Override
	public void setPersonalPassword(String personalPassword) throws IntegrationModuleException {
		//Do nothing
	}

}
